import './Admin_login.css';
import { Navbar,Nav,Form,Table,Carousel,Button,Card,Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link,Redirect } from 'react-router-dom';
import { useState,useEffect } from 'react';
import {db,auth} from '../firebase';
function App() {
  const [email,setEmail]=useState('');//database
  const [password,setPassword]=useState('');//database
  const [uemail,setUemail]=useState('');
  const [upassword,setUpassword]=useState('');

  useEffect(() =>{
    db.collection('AdminDetails').onSnapshot(snapshot =>{
      setEmail(snapshot.docs.map(doc => doc.data().Email))
      setPassword(snapshot.docs.map(doc => doc.data().Password))
    })
  },[]);

  function check() {
    if(email===uemail){
      return <Redirect to="/login" />
    }
    else{
      <Alert variant="danger">
        Wrong credentials try again !
      </Alert>
    }
  }

  return (
   <div className="Admin">
       <Link to="/"><Button variant="primary" className="but">Home</Button></Link>
       <div className="Guns">
       <Card border="dark" style={{ width: '30rem' }}>
        <Card.Body>
          <Card.Title>Admin LOGIN</Card.Title>
          <Form>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="email" onChange={e => setUemail(e.currentTarget.value)}/>
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="password" onChange={e => setUpassword(e.currentTarget.value)}/>
            </Form.Group>
            <Button variant="primary" type="submit" onClick={check()}>Submit</Button>
            <Card.Link href="#" className="llink">Forgot password</Card.Link>
          </Form>
        </Card.Body>
      </Card>
      </div>
  </div>
  );
}

export default App;
