import './Admin_login.css';
import { Navbar,Nav,Form,Table,Carousel,Button,Card,Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link,Redirect } from 'react-router-dom';
import { useState,useEffect } from 'react';
import {db,auth} from '../firebase';
function App() {
  const [email,setEmail]=useState('');//database
  const [password,setPassword]=useState('');//database
  const [uemail,setUemail]=useState('');
  const [upassword,setUpassword]=useState('');

  useEffect(() =>{
    db.collection('AdminDetails').onSnapshot(snapshot =>{
      setEmail(snapshot.docs.map(doc => doc.data().Email))
      setPassword(snapshot.docs.map(doc => doc.data().Password))
    })
  },[]);

  function check() {
    if(email===uemail){
      <Redirect to="/ahome" />
    }
    else{
      <Alert variant="danger">
        Wrong credentials try again !
      </Alert>
    }
  }

  return (
   <div className="Admin">
       <Link to="/"><Button variant="primary" className="but">Home</Button></Link>
       <div className="Guns">
       <Card border="dark" style={{ width: '30rem' }}>
        <Card.Body>
          <Card.Title>Admin LOGIN</Card.Title>
          <form id="login-patient" onSubmit={(e)=>submit_login(e)}>
               <br></br>
                <label>
                  Email ID:
                    <input type="text" id="uname" className='box1inp'/>
                </label>
                <br></br>
                <label>
                  <br></br>
                  Password:
                    <input type="password" id="pwd" className='box1inp'/>
                </label>
                <br></br>
                
                <input type="submit" value="Submit" className='submit' />
                <br></br>
                <br></br>
            <br></br>
                <div id="data"></div>
            </form>
        </Card.Body>
      </Card>
      </div>
  </div>
  );
}
function submit_login(e){
  e.preventDefault();
  let request =  {
      username:document.getElementById('uname').value,
      pass:document.getElementById('pwd').value,
  }

}
export default App;
