import './Admin_login.css';
import { Navbar,Nav,Form,Table,Carousel,Button,Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import { useState,useEffect } from 'react';
import {db,auth} from '../firebase';
function App() {
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [username,setUsername]=useState('');
  const state ={
    Admin: null
  }

  function componentdidMount(){
    db.collection('AdminDetails').get().then(snapshot =>{
      console.log(snapshot);
    })
    .catch(error => console.log(error));
  }

  return (
   <div className="Admin">
       <Link to="/"><Button variant="primary" className="but">Home</Button></Link>
       <div className="Guns">
       <Card border="dark" style={{ width: '30rem' }}>
        <Card.Body>
          <Card.Title>Admin LOGIN</Card.Title>
          <Form>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email"  onChange={e => setEmail(e.currentTarget.value)}/>
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" onChange={e => setPassword(e.currentTarget.value)}/>
            </Form.Group>
            <Button variant="primary" type="submit" >Submit</Button>
            <Card.Link href="#" className="llink">Forgot password</Card.Link>
          </Form>
        </Card.Body>
      </Card>
      </div>
  </div>
  );
}

export default App;
